function []=animate_gesture(gesture,tags, pause_time,label)
% fully animates a gesture from beginning to end in the current axes
% The gesture must be a struct as generated by the function generate_gestures
% label to add to the gesture at all times

if (nargin<2)
    tag='Class tags not specified';
else
    tag=tags{gesture.y};
end

if (nargin<3)
    pause_time=1/30;
end

if (nargin<4)
    label='';
end


label=[label ' - ' tag];
clf;
h=axes;
frames=size(gesture.x,1);
for frame=1:frames
    cla;
    
	visualize_skeleton(gesture.x,frame,label,h);
	drawnow;
	pause(pause_time);
    if (frame==gesture.detection_frame)
        fprintf('Gesture Detected: %s \n', tags{gesture.y});
    end
end

